    public contacts contact { get; set; } = new contacts();

    public bool IsSaved = false;
    public bool IsError = false;
    public bool IsValidationError = false;

    public async Task SubmitForm()
    {
        if (ValidateForm())
        {
            IsValidationError = false;
            contact.id = Guid.NewGuid().ToString();
            var response = await _client.PostAsJsonAsync<contacts>("/", contact);
            if (response.IsSuccessStatusCode)
            {
                IsSaved = true;
                IsError = false;
            }
            else
            {
                IsSaved = false;
                IsError = true;
            }
        }
        else
        {
            IsSaved = false;
            IsError = false;
            IsValidationError = true;
        }
    }

    public bool ValidateForm()
    {
        return (!string.IsNullOrEmpty(contact.Name) &&
        !string.IsNullOrEmpty(contact.Email) &&
        !string.IsNullOrEmpty(contact.Subject) &&
        !string.IsNullOrEmpty(contact.Message));
    }

    public void ToggleFlag(EventArgs args)
    {
        IsSaved = !IsSaved;
    }

    public void ToggleErrorFlag(EventArgs args)
    {
        IsError = !IsError;
    }

    public void ToggleValidationErrorFlag(EventArgs args)
    {
        IsValidationError = !IsValidationError;
    }

